    public void getSystemArticle(int num,String cid) {
        // 为了省事，这里直接回调成功
        ApiHelper.getWanAndroidApi()
                .getSystemArticle(num,cid)
                .compose(Transformer.switchSchedulers())
                .subscribe(new DataObserver<SystemArticleModel>() {
                    @Override
                    protected void onError(String errorMsg) {
                        getListener().onSystemArticleFail(errorMsg);
                    }

                    @Override
                    protected void onSuccess(SystemArticleModel data) {
                        getListener().onSystemArticleSucceed(data);
                    }
                });
    }

