    public void getProjectArticle(int num, String cid) {
        // 为了省事，这里直接回调成功
        ApiHelper.getWanAndroidApi()
                .getProjectArticle(num, cid)
                .compose(Transformer.switchSchedulers())
                .subscribe(new DataObserver<ProjectArticleModel>() {
                    @Override
                    protected void onError(String errorMsg) {
                        getListener().onProjectArticleFail(errorMsg);
                    }

                    @Override
                    protected void onSuccess(ProjectArticleModel data) {
                        getListener().onProjectArticleSucceed(data);
                    }
                });
    }

